function eqs = retrieve_eqs(a1,a2,a3,c1,c2,c3,c4,c5,c6,c7,c8,c9,c10,c11,c12,c13,c14,c15,c16,c17,c18) 
eqs(1) = a1^2*c1^2 + a1^2*c2^2 + a1^2*c3^2 - 2*a1*a2*c1*c4 - 2*a1*a2*c2*c5 - 2*a1*a2*c3*c6 + a2^2*c4^2 + a2^2*c5^2 + a2^2*c6^2 - c10^2 + 2*c10*c13 - c11^2 + 2*c11*c14 - c12^2 + 2*c12*c15 - c13^2 - c14^2 - c15^2;
eqs(2) = a1^2*c1^2 + a1^2*c2^2 + a1^2*c3^2 - 2*a1*a3*c1*c7 - 2*a1*a3*c2*c8 - 2*a1*a3*c3*c9 + a3^2*c7^2 + a3^2*c8^2 + a3^2*c9^2 - c10^2 + 2*c10*c16 - c11^2 + 2*c11*c17 - c12^2 + 2*c12*c18 - c16^2 - c17^2 - c18^2;
eqs(3) = a2^2*c4^2 + a2^2*c5^2 + a2^2*c6^2 - 2*a2*a3*c4*c7 - 2*a2*a3*c5*c8 - 2*a2*a3*c6*c9 + a3^2*c7^2 + a3^2*c8^2 + a3^2*c9^2 - c13^2 + 2*c13*c16 - c14^2 + 2*c14*c17 - c15^2 + 2*c15*c18 - c16^2 - c17^2 - c18^2;

end